svg.script:
var nAgt = navigator.userAgent;
var browserName = navigator.appName;

// Simple detection logic
if (nAgt.indexOf("Firefox") != -1) browserName = "Mozilla Firefox";
else if (nAgt.indexOf("Chrome") != -1) browserName = "Google Chrome";
else if (nAgt.indexOf("Safari") != -1) browserName = "Apple Safari";
else if (nAgt.indexOf("Edg") != -1) browserName = "Microsoft Edge";

document.getElementById('browserName').textContent = browserName;
